--
-- Definition of table `chapter`
--

DROP TABLE IF EXISTS `chapter`;
CREATE TABLE `chapter` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `course_id` int(10) unsigned DEFAULT NULL COMMENT '课程id',
  `title` varchar(55) NOT NULL COMMENT '标题 ',
  `content` text NOT NULL COMMENT '内容',
  `content_pic` varchar(200) DEFAULT NULL COMMENT '内容中图片',
  `is_free` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否试听允许为1不允许为0',
  `vod_path` varchar(200) DEFAULT NULL COMMENT '视频文件',
  `keywords` varchar(100) DEFAULT NULL COMMENT '关键字',
  `dates` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '日期',
  `abstracts` varchar(200) DEFAULT NULL COMMENT '简述',
  PRIMARY KEY (`id`),
  KEY `FK_chapter_3` (`course_id`) USING BTREE,
  CONSTRAINT `FK_chapter_1` FOREIGN KEY (`course_id`) REFERENCES `course` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=28 DEFAULT CHARSET=gbk COMMENT='课程内容表';

