--
-- Definition of table `course`
--

DROP TABLE IF EXISTS `course`;
CREATE TABLE `course` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `title` varchar(55) NOT NULL COMMENT '课程名称',
  `isrecom` tinyint(1) unsigned DEFAULT '0' COMMENT '是否推荐',
  `sorts` tinyint(1) unsigned NOT NULL DEFAULT '9' COMMENT '排序',
  `picture` varchar(55) DEFAULT NULL COMMENT '图片',
  `islock` tinyint(1) unsigned DEFAULT '0' COMMENT '是否锁定',
  `counts` int(10) unsigned DEFAULT '0' COMMENT '点击次数',
  `dates` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '时间',
  `descs` text NOT NULL COMMENT '课程描述',
  `type_id` int(10) unsigned DEFAULT NULL COMMENT '类别id',
  `keyword` varchar(55) NOT NULL COMMENT '关键字',
  PRIMARY KEY (`id`),
  KEY `FK_course_1` (`type_id`),
  CONSTRAINT `FK_course_1` FOREIGN KEY (`type_id`) REFERENCES `course_type` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=gbk COMMENT='系列课程名';

