DROP TABLE IF EXISTS `download`;
CREATE TABLE `download` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `type_id` int(10) unsigned DEFAULT NULL COMMENT '类别id',
  `title` varchar(45) NOT NULL COMMENT '标题',
  `descs` text COMMENT '描述',
  `sizes` varchar(10) DEFAULT '0' COMMENT '大小',
  `dates` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '日期',
  `counts` int(10) unsigned DEFAULT NULL COMMENT '下载资数',
  `platform` varchar(45) DEFAULT NULL COMMENT '应用平台',
  `soft_file` varchar(55) DEFAULT NULL COMMENT '下载路径',
  `keyword` varchar(50) DEFAULT NULL COMMENT '关键字',
  `old_name` varchar(55) DEFAULT NULL COMMENT '原始名',
  `language` varchar(55) DEFAULT NULL COMMENT '语言',
  `ispop` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '0不推荐1推荐',
  `contentType` varchar(55) DEFAULT NULL COMMENT '类型',
  `picture` varchar(50) DEFAULT NULL COMMENT '首页图片',
  `ispass` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '0未审核1审核',
  `isfree` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '授权',
  `member_id` int(10) unsigned DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `FK_download_1` (`type_id`),
  KEY `FK_download_2` (`member_id`),
  CONSTRAINT `FK_download_1` FOREIGN KEY (`type_id`) REFERENCES `down_type` (`id`),
  CONSTRAINT `FK_download_2` FOREIGN KEY (`member_id`) REFERENCES `member` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=28 DEFAULT CHARSET=gbk COMMENT='下载';

