DROP TABLE IF EXISTS `finance`;
CREATE TABLE `finance` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `member_id` int(10) unsigned DEFAULT NULL COMMENT '用户id',
  `from_id` int(10) unsigned DEFAULT NULL COMMENT '来源用户id',
  `money` int(11) NOT NULL DEFAULT '0' COMMENT '钱',
  `pay_type` tinyint(1) DEFAULT '-1' COMMENT '0购买1兑现2充值3提成4收入',
  `descs` varchar(45) DEFAULT NULL COMMENT '描述',
  `dates` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '日期',
  `order_account` varchar(50) DEFAULT NULL COMMENT '''订单号''',
  PRIMARY KEY (`id`),
  KEY `FK_finance_1` (`member_id`),
  CONSTRAINT `FK_finance_1` FOREIGN KEY (`member_id`) REFERENCES `member` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=gbk COMMENT='财务表';

