--
-- Definition of table `member`
--

DROP TABLE IF EXISTS `member`;
CREATE TABLE `member` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `level_id` int(10) unsigned DEFAULT NULL COMMENT '级别id',
  `email` varchar(65) NOT NULL COMMENT 'eamil',
  `password` varchar(65) NOT NULL COMMENT '密码',
  `name` varchar(10) NOT NULL COMMENT '姓名',
  `qq` varchar(15) NOT NULL COMMENT 'qq',
  `cellphone` varchar(45) NOT NULL COMMENT '手机',
  `integal` int(10) unsigned DEFAULT '10' COMMENT '积分',
  `acount` varchar(45) DEFAULT '0.00' COMMENT '帐户',
  `acount_type` tinyint(1) unsigned DEFAULT '0' COMMENT '帐户类别',
  `balance` int(10) unsigned DEFAULT '0' COMMENT '余额',
  `recom_id` int(10) unsigned DEFAULT NULL COMMENT '推荐人id',
  `dates` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `islock` tinyint(1) unsigned DEFAULT '0',
  `privileges` varchar(45) DEFAULT '111' COMMENT '用户权限',
  PRIMARY KEY (`id`),
  KEY `FK_member_1` (`level_id`),
  KEY `FK_member_2` (`recom_id`) USING BTREE,
  CONSTRAINT `FK_member_1` FOREIGN KEY (`level_id`) REFERENCES `memberlevel` (`id`),
  CONSTRAINT `FK_member_2` FOREIGN KEY (`recom_id`) REFERENCES `member` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=60 DEFAULT CHARSET=gbk COMMENT='会员';

