--
-- Definition of table `question`
--

DROP TABLE IF EXISTS `question`;
CREATE TABLE `question` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `type_id` int(10) unsigned DEFAULT NULL COMMENT '问题类别',
  `title` varchar(45) NOT NULL COMMENT '问题标题',
  `descs` text COMMENT '问题内容',
  `dates` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '发布日期',
  `integral` int(10) unsigned DEFAULT '0' COMMENT '悬赏积分',
  `ispop` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否推荐',
  `member_id` int(10) unsigned DEFAULT NULL COMMENT '用户id',
